import React, { Component } from "react";
import axios from "axios";

class IdeaSubmission extends Component {
  state = {};

  formSubmitHandler = (e) => {
    e.preventDefault();
    const form = document.querySelector("#ideaSubmission");
    const data = new FormData(form);
    data.append(e.target.elements.username.value);
    data.append(e.target.elements.emailid.value);
    data.append(e.target.elements.contact.value);
    data.append(e.target.elements.ideaContent.value);
    console.log(e.target.elements.ideaContent.value);
    console.log(data);
    axios
      .post("http://127.0.0.1:8000/api-blog/ideas/", data)
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        alert(err);
        console.log(err);
      });
  };

  render() {
    return (
      <form
        onSubmit={this.formSubmitHandler}
        name="ideaForm"
        id="ideaSubmission"
      >
        <label>
          Name: <input type="text" name="username" />
        </label>
        <label>
          Email Id: <input type="email" name="emailid" />
        </label>
        <label>
          Contact: <input type="text" name="contact" />
        </label>
        <label>
          Idea: <textarea name="ideaContent"></textarea>
        </label>
        <button className="btn btn-primary" type="submit">
          Submit
        </button>
      </form>
    );
  }
}

export default IdeaSubmission;
