import React from "react";
import { Link } from "react-router-dom";
import "./css/slider.css";

function Slider(props) {
  function slideLeft() {
    const scrollLength = document.querySelector("img").length;
    console.log(scrollLength);
    document
      .querySelector("#slide-div")
      .scrollBy({ left: scrollLength, behavior: "smooth" });
  }
  function slideRight() {
    const scrollLength = document.querySelector(".slider-img").length;
    document
      .querySelector("#slide-div")
      .scrollBy({ left: -1 * scrollLength, behavior: "smooth" });
  }
  return (
    <div id="container-div">
      <div id="slide-div">
        <div id="img-list" className="d-flex">
          {props.imgUrl.map((image) => {
            return (
              <div className="slide-img m-2">
                <Link>
                  <img src={image} alt="image" className="slider-img" />
                </Link>
              </div>
            );
          })}
        </div>
      </div>
      <button
        id="left-slide-btn"
        className="bg-transparent"
        onClick={() => slideLeft()}
      >
        (--
      </button>
      <button
        id="right-slide-btn"
        className="bg-transparent"
        onClick={() => slideRight()}
      >
        --)
      </button>
    </div>
  );
}

export default Slider;
