import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Helmet } from "react-helmet";

class BlogList extends Component {
  state = {
    blogsList: [],
    category: "",
  };

  componentDidMount() {
    axios
      .get("http://127.0.0.1:8000/api-blog/")
      .then((res) => {
        this.setState({
          blogsList: res.data,
        });
      })
      .catch((err) => {
        <div>Something Went Wrong. Please Re-Try </div>;
        console.log(err);
      });
  }

  clickHandler = (e) => {
    this.setState(() => {
      return {
        category: e.target.attributes.value.value,
      };
    });
    console.log(this.state.category);
    console.log(this.state.blogsList);
  };

  render() {
    return (
      <>
        <Helmet>
          <title>Blogs</title>
        </Helmet>
        <label>
          Categories
          <Link to={`/blogs/${this.state.category}`}>
            <button
              onClick={this.clickHandler}
              value="entertainment"
              category={this.state.category}
            >
              Entertainment
            </button>
          </Link>
        </label>
        {this.state.blogsList.map((blog) => {
          return (
            <Link to={`/blogs/${blog.id}`} key={blog.id}>
              <div>
                <h2>{blog["description"]}</h2>
              </div>
            </Link>
          );
        })}
      </>
    );
  }
}

export default BlogList;
