import React, { Component } from "react";
import axios from "axios";
// import React, { useEffect } from "react";
import { Helmet } from "react-helmet";
import CardText from "../Components/cardWithText.jsx";
import SectionWithImg from "../Components/sectionWithImage.jsx";
import SectionWithBgImg from "../Components/sectionBgImage.jsx";
import whyOytPic from "../Images/whyOYT.png";
import "./Career.css";
import whyOytBg from "../Images/whyOytBg.png";
import positionBg from "../Images/positionsBg.png";

class CareerPage extends Component {
  state = {
    jobs: [],
  };
  componentDidMount = () => {
    axios
      .get("http://127.0.0.1:8000/api-blog/jobs")
      .then((res) => {
        console.log(res.data);
        this.setState({
          jobs: res.data,
        });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  render() {
    return (
      <>
        {" "}
        <Helmet>
          <title>Career</title>
        </Helmet>
        <SectionWithBgImg
          heading="Why OYT?"
          text={
            <div className="d-flex justify-content-center">
              <p>
                Lorem Ipsum is simply dummy text of the printing and typesetting
                industry. Lorem Ipsum has been the industry's standard dummy
                text ever since the 1500s, when an unknown printer took a galley
                of type and scrambled it to make a type specimen book. It has
                survived not only five centuries, but also the leap into
                electronic typesetting, remaining essentially unchanged. It was
                popularised in the 1960s with the release of Letraset sheets
                containing Lorem Ipsum passages, and more recently with desktop
                publishing software like Aldus PageMaker including versions of
                Lorem Ipsum.
              </p>
            </div>
          }
          imgUrl={whyOytBg}
        />
        <SectionWithImg
          heading="How to reach us"
          text={
            <div className="d-md-flex justify-content-between">
              <p>
                Lorem Ipsum is simply dummy text of the printing and typesetting
                industry. Lorem Ipsum has been the industry's standard dummy
                text ever since the 1500s, when an unknown printer took a galley
                of type and scrambled it to make a type specimen book. It has
                survived not only five centuries, but also the leap into
                electronic typesetting, remaining essentially unchanged. It was
                popularised in the 1960s with the release of Letraset sheets
                containing Lorem Ipsum passages, and more recently with desktop
                publishing software like Aldus PageMaker including versions of
                Lorem Ipsum.
              </p>
              <img
                src={whyOytPic}
                alt="whyOytPic"
                className="d-none d-md-block rounded"
                style={{ width: "40%" }}
              />
            </div>
          }
        />
        <div className="container row">
          <h2>Current Openings</h2>
          <hr />
          <div className="col-md-6 position-div">
            {this.state.jobs.map((job) => {
              return (
                <CardText
                  superHead={job.title}
                  heading={<small>{job.place}</small>}
                  bgimg={positionBg}
                  text={
                    <ul className="p-0">
                      <div className="d-flex flex-wrap">
                        {job.skills
                          .trim()
                          .split(",")
                          .map((skill) => {
                            return (
                              <li className="px-2">
                                <i class="bi bi-dot"></i>
                                {skill}
                              </li>
                            );
                          })}
                      </div>
                    </ul>
                  }
                />
              );
            })}
          </div>
        </div>
      </>
    );
  }
}

