import React, { Component } from "react";

class JobApplicationForm extends Component {
  state = {};

  formSubmitHandler = () => {
    e.preventDefault();
    const name = e.target.elements.name.value;
    const contact = e.target.elements.contact.value;
    const query = e.target.elements.query.value;
    axios
      .post("http://127.0.0.1:8000/api-blog/query/", {
        name: name,
        contact: contact,
        query: query,
      })
      .then((res) => {
        alert(
          res.statusText === "Created"
            ? "Your query is Submitted. We will soon revert back to you."
            : "Please, Try again later!!"
        );
        console.log(res);
      })
      .catch((err) => {
        alert("Your Query not Submitted. Please Try again!!" + err);
        console.log(err);
      });
  };
  render() {
    return (
      <form
        className="d-flex flex-column align-items-center m-5"
        onSubmit={this.formSubmitHandler}
      >
        <label>
          Name:
          <input
            className="mx-2"
            type="text"
            name="name"
            placeholder="your Name"
          />
        </label>
        <label>
          Email Id:
          <input
            className="mx-2"
            type="email"
            name="emailId"
            placeholder="Your Email address. Ex: somemail@gmail.com"
          />
        </label>
        <label>
          Contact:
          <input
            className="mx-2"
            type="text"
            name="contact"
            placeholder="Your active mobile no."
          />
        </label>
        <label>
          Position:
          <input
            className="mx-2"
            type="text"
            name="position"
            value={this.props.location.state}
          />
        </label>
        <label>
          Experience:
          <input type="number" name="experience" className="mx-2" />
        </label>
        <label>
          Attach your Resume/Cv :
          <input type="file" name="resume_cv" className="mx-2" />
        </label>
        <button type="submit" className="btn btn-primary">
          Apply
        </button>
      </form>
    );
  }
}

export default JobApplicationForm;
