import React, { useEffect } from "react";
import CardText from "../Components/cardWithText.jsx";
import SectionWithImg from "../Components/sectionWithImage.jsx";
import SectionWithBgImg from "../Components/sectionBgImage.jsx";
import whyOytPic from "../Images/whyOYT.png";
import "./Career.css";
import whyOytBg from "../Images/whyOytBg.png";
import positionBg from "../Images/positionsBg.png";

function CareerPage() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);
  function handleClick(index01) {
    // console.log(index01);
    document.querySelector(`#pos${index01}`).classList.toggle("showInfo");
  }
  const positions = [
    {
      position0: [
        "UI-centric approach",
        "AI and Machine learning",
        "Consider the crucial role of sensors",
        "Consider",
        "hello",
      ],
    },
    { position1: ["skill1", "skljgjfk", "hello", "hello", "hello", "hello"] },
    { position0: ["skill1", "skill2", "hello", "hello"] },
    { position0: ["skill1", "skill2"] },
    { position1: ["skill1", "skljgjfk"] },
    { position1: ["skill1", "skljgjfk"] },
  ];
  return (
    <React.Fragment>
      <title>Career</title>
      <SectionWithBgImg
        heading="Why OYT?"
        text={
          <div className="d-flex justify-content-center">
            <p>
              Lorem Ipsum is simply dummy text of the printing and typesetting
              industry. Lorem Ipsum has been the industry's standard dummy text
              ever since the 1500s, when an unknown printer took a galley of
              type and scrambled it to make a type specimen book. It has
              survived not only five centuries, but also the leap into
              electronic typesetting, remaining essentially unchanged. It was
              popularised in the 1960s with the release of Letraset sheets
              containing Lorem Ipsum passages, and more recently with desktop
              publishing software like Aldus PageMaker including versions of
              Lorem Ipsum.
            </p>
          </div>
        }
        imgUrl={whyOytBg}
      />
      <SectionWithImg
        heading="How to reach us"
        text={
          <div className="d-md-flex justify-content-between">
            <p>
              Lorem Ipsum is simply dummy text of the printing and typesetting
              industry. Lorem Ipsum has been the industry's standard dummy text
              ever since the 1500s, when an unknown printer took a galley of
              type and scrambled it to make a type specimen book. It has
              survived not only five centuries, but also the leap into
              electronic typesetting, remaining essentially unchanged. It was
              popularised in the 1960s with the release of Letraset sheets
              containing Lorem Ipsum passages, and more recently with desktop
              publishing software like Aldus PageMaker including versions of
              Lorem Ipsum.
            </p>
            <img
              src={whyOytPic}
              alt="whyOytPic"
              className="d-none d-md-block rounded"
              style={{ width: "40%" }}
            />
          </div>
        }
      />
      <div className="container">
        <h2>Current Openings</h2>
        <hr />
        <div className="row">
          {positions.map((position, index01) => {
            return (
              <div
                className="col-md-6 position-div"
                // style={{ backgroundImage: `url("${positionBg}")` }}
              >
                <CardText
                  superHead={Object.keys(position)[0]}
                  heading=""
                  bgimg={positionBg}
                  text={
                    <div>
                      <small>Jaipur, Rajasthan</small>
                      <ul className="p-0">
                        <div className="">
                          {Object.values(position).map((skill) => {
                            return (
                              <div className="row">
                                {skill.map((s, i) => {
                                  return (
                                    <div className="col-md-6">
                                      <li className="px-2" key={i}>
                                        <i class="bi bi-dot"></i>
                                        {s}
                                      </li>
                                    </div>
                                  );
                                })}
                              </div>
                            );
                          })}
                        </div>
                      </ul>
                      <button
                        className="btn btn-primary"
                        id={index01}
                        onClick={(e) => handleClick(e.target.id)}
                      >
                        Apply
                      </button>
                      <div className="showInfo" id={`pos${index01}`}>
                        Mail Your Resume/CV @ somemail.com
                      </div>
                    </div>
                  }
                />
              </div>
            );
          })}
        </div>
      </div>
    </React.Fragment>
  );
}

export default CareerPage;
