import React from "react";
import { Link } from "react-router-dom";
import "./css/slider.css";

function Slider(props) {
  function handleLeftSlide() {
    console.log("slideLeft");
  }
  function handleRightSlide() {
    document.querySelector(".imgDiv").scrollBy(160, 0);
  }
  return (
    <div id="slider-div" className="d-flex container">
      <div className="imgDiv d-flex">
        {props.imgUrl.map((i, index) => {
          return (
            <div className="slider-img m-2">
              <Link to={props.link[index]}>
                <img key={index} src={i} alt="projectImg" />
              </Link>
            </div>
          );
        })}
      </div>
      <button
        className="bg-transparent"
        id="left-slide-btn"
        onClick={() => handleLeftSlide()}
      >
        <i class="bi bi-arrow-left-square-fill"></i>
      </button>
      <button
        className="bg-transparent"
        id="right-slide-btn"
        onClick={() => handleRightSlide()}
      >
        <i class="bi bi-arrow-right-square-fill"></i>
      </button>
    </div>
  );
}

export default Slider;
