import React from "react";
import bgVideo from "../Images/About.mp4";
import "./css/videoBg.css";
import SocialMediaIcons from "./socialIcons";

function VideoBgSection() {
  const caseStudy = 21;
  const projects = 8;
  const rnd = 35;

  function animateValue(obj, start, end, duration) {
    let startTimestamp = null;
    const step = (timestamp) => {
      if (!startTimestamp) startTimestamp = timestamp;
      const progress = Math.min((timestamp - startTimestamp) / duration, 1);
      obj.innerHTML = Math.floor(progress * (end - start) + start);
      if (progress < 1) {
        window.requestAnimationFrame(step);
      }
    };
    window.requestAnimationFrame(step);
  }

  const obj = document.getElementById("value");
  animateValue(obj, 0, rnd, 5000);

  return (
    <React.Fragment>
      <div className="d-flex justify-content-center text-center videoBg-div">
        <video src={bgVideo} autoPlay muted loop className="ms-auto"></video>
        <div className="text-on-video text-white">
          <h1 className="my-3">About Us</h1>
          <h2 className="">Welcome to OxYmore Technology Pvt. Ltd.</h2>
          <div className="d-sm-flex justify-content-between my-5">
            <div className="text-center mx-5">
              <SocialMediaIcons sMedia="briefcase-fill fs-2" />
              <p className="my-3 fs-2">{caseStudy}</p>
              <p>
                <small>CASE STUDIES</small>
              </p>
            </div>
            <div className="text-center mx-5">
              <SocialMediaIcons sMedia="stack fs-2" />
              <p className="my-3 fs-2 numbers">
                <span>{projects}</span>
              </p>
              <p>
                <small>PROJECTS</small>
              </p>
            </div>
            <div className="text-center mx-5">
              <SocialMediaIcons sMedia="gear-fill fs-2" />
              <p className="my-3 fs-2">{rnd}</p>
              <small>
                <p className="m-0">RESEARCH AND PRODUCT </p>DEVELOPMENT
              </small>
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}

export default VideoBgSection;
