import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Helmet } from "react-helmet";

class BlogCategoryView extends Component {
  state = {
    blogList: [],
  };

  componentDidMount() {
    axios
      .get("http://127.0.0.1:8000/api-blog/")
      .then((res) => {
        this.setState({
          blogsList: res.data,
        });
      })
      .catch((err) => {
        <div>Something Went Wrong. Please Re-Try </div>;
        console.log(err);
      });
  }

  render() {
    return (
      <div>
        {/* {console.log(this.props.category)}
        {console.log(this.state.blogList)} */}
        {this.state.blogList.map((blog) => {
          if (blog.category === props.category) {
            return (
              <Link to={`/blogs/${blog.id}`} key={blog.id}>
                <div>
                  <h2>{blog["description"]}</h2>
                </div>
              </Link>
            );
          }
        })}
      </div>
    );
  }
}

export default BlogCategoryView;
